document.addEventListener("page:loaded",()=>{document.querySelectorAll(".post-body :not(a) > img, .post-body > img").forEach(t=>{const e=t.dataset.src||t.src;const a=document.createElement("a");a.classList.add("fancybox");a.href=e;a.setAttribute("itemscope","");a.setAttribute("itemtype","http://schema.org/ImageObject");a.setAttribute("itemprop","url");let o="default";if(t.closest(".post-gallery")!==null){o="gallery"}else if(t.closest(".group-picture")!==null){o="group"}a.dataset.fancybox=o;const s=t.title||t.alt;if(s){a.title=s;a.dataset.caption=s}t.wrap(a)});Fancybox.bind("[data-fancybox]")});